package com.example.everyevent.RecentEventList;import java.text.ParseException;import java.util.Date;public class RecentList {    private int index;    private String userInfo;    private String title;    private String contents;    private String address;    private Date startDate;    private long numberOfApplicants = 0;    private long numberOfAttendees = 0;    private String numberOfPeopleCanApply;    private boolean running = false;    private long leftDays;    private String documentId;    public RecentList(int index, String userInfo, String title, String contents, String address, Date startDate, String numberOfPeopleCanApply, String documentId, long numberOfApplicants, long numberOfAttendees) throws ParseException {        this.index = index;        this.userInfo = userInfo;        this.title = title;        this.contents = contents;        this.address = address;        this.startDate = startDate;        this.numberOfPeopleCanApply = numberOfPeopleCanApply;        this.documentId = documentId;        this.numberOfApplicants = numberOfApplicants;        this.numberOfAttendees = numberOfAttendees;    }    public int getIndex() { return this.index; }    public void setIndex(int index) { this.index = index; }    public String getTitle(){        return this.title;    }    public void setTitle(String title) {        this.title = title;    }    public String getAddress(){        return this.address;    }    public void setAddress(String address) {        this.address = address;    }    public Date getStartDate(){        return this.startDate;    }    public void setStartDate(Date startDate) {        this.startDate = startDate;    }    public long getNumberOfApplicants() { return this.numberOfApplicants; }    public void setNumberOfApplicants(int numberOfApplicants) {this.numberOfApplicants = numberOfApplicants;}    public String getNumberOfPeopleCanApply() { return this.numberOfPeopleCanApply; }    public void setNumberOfPeopleCanApply(String numberOfPeopleCanApply) {this.numberOfPeopleCanApply = numberOfPeopleCanApply; }    public long getNumberOfAttendees() { return this.numberOfAttendees; }    public void setNumberOfAttendees(int numberOfAttendees) { this.numberOfAttendees = numberOfAttendees; }    public boolean getRunning() { return this.running;}    public void setRunning (boolean running) { this.running = running;}    public String getContents() { return this.contents; }    public void setContents(String contents) { this.contents = contents; }    public String getUserInfo() { return userInfo; }    public void setUserInfo(String numberOfPeopleCanApply) { this.userInfo = userInfo; }    public long getLeftDays() { return this.leftDays; }    public void setLeftDays(long leftDays){ this.leftDays = leftDays; }    public String getDocumentId(){ return this.documentId; }    public void setDocumentId(String documentId) {this.documentId = documentId; }}